import { Component } from 'react';
import { Col } from 'react-bootstrap';
import { Row } from 'react-bootstrap';
import { connect, RootStateOrAny } from 'react-redux';
import { actions } from '../actions and const/actions';

const symbolsForCacl: any[] = [1, 2, 3, 4, 5, 6, 7, 8, 9, 0];

// numbers its years
const symbolsForMortgegeLength: number[] = [10, 15, 20, 25, 30];

class CalculatorApartment extends Component<any> {


    takePriceInMouth = () => {
        const apartment = this.props.apartments[this.props.indexOfSelectedApartment]
    
    }
    

    render() {

        this.takePriceInMouth()
        const calculatingOfMortgege = () => {
            const fullPrice = 1;
            const quantityOfMonths = 1;
            const annualInterestRateInMouths = 1;

            this.props.showCaseApartment.map((apartment: any, indexApartment: number) => {
                if (indexApartment == this.props.indexOfSelectedApartment) {
                    return <div>5435</div>;
                }
            });

            // fullPrice / quantityOfMonths + fullPrice * annualInterestRateInMouths
        };

        return (
            <Row>
                <Col lg={{ span: 10, offset: 1 }}>
                    <div className="calculatorApartment-body">
                        <div className="calculatorApartment-priceInMouth">
                            <div className="calculatorApartment-priceInMouth-string">priceInMouth</div>

                            {this.props.symbolsForCaclСounting.map((item: any) => (
                                <div className="calculatorApartment-priceInMouth-symbols">{item}</div>
                            ))}
                        </div>

                        <div className="calculatorApartment-priceInYear">
                            <div className="calculatorApartment-priceInYear-string">priceInYear</div>
                        </div>

                        <div className="calculatorApartment-buttonsOfCalculator">
                            <div className="calculatorApartment-buttonsOfCalculator-textarea">
                                <div className="calculatorApartment-monthlyRate-symbols-leftSite">Your monthly rate</div>
                                {this.props.symbolsForCaclСounting.map((item: any) => (
                                    <div className="calculatorApartment-monthlyRate-symbols-rightSite">{item}</div>
                                ))}
                            </div>

                            {symbolsForCacl.map((symbol: any) => {
                                return (
                                    <div onClick={() => this.props.setSymbolForCacl(symbol)} className="calculatorApartment-buttonsOfCalculator-symbolForCacl">
                                        {symbol}
                                    </div>
                                );
                            })}
                        </div>

                        <div className="calculatorApartment-selectDistantOfMortgage">
                            <div>{this.props.mortgegeLength}</div>

                            {symbolsForMortgegeLength.map((symbol: number) => (
                                <div className="calculatorApartment-selectDistantOfMortgage-symbols" onClick={() => this.props.setMortgegeLength(symbol)}>
                                    {symbol}
                                </div>
                            ))}
                        </div>
                    </div>
                </Col>
            </Row>
        );
    }
}

let mapStateToProps = (state: RootStateOrAny) => ({
    apartments: state.apartmentsArray.apartments,
    indexOfSelectedApartment: state.apartmentsArray.indexOfSelectedApartment,
    symbolsForCaclСounting: state.calculatorApartment.symbolsForCaclСounting,
    mortgegeLength: state.calculatorApartment.mortgegeLength,

});

export default connect(mapStateToProps, { setSymbolForCacl: actions.setSymbolForCacl, setMortgegeLength: actions.setMortgegeLength })(CalculatorApartment);
