import { Component, useEffect, useState } from 'react';
import { Col } from 'react-bootstrap';
import { Row } from 'react-bootstrap';
import { connect, RootStateOrAny } from 'react-redux';

const WhatsOutside = (props: any) => {
    const apartment = props.apartments[props.indexOfSelectedApartment];

    const [positionOfImg, setPositionOfImg] = useState(0);
    const [intervalId, setIntervalId] = useState<NodeJS.Timeout>();
    const [recursiveTimeoutId, setRecursiveTimeoutId] = useState<any>();
    const [recursiveCheackTimerId, setRecursiveCheackTimerId] = useState<any>();
    const [recursiveTimeoutIsWorking, setRecursiveTimeoutIsWorking] = useState<boolean>(true);

    console.log(`recursiveTimeoutId = ${recursiveTimeoutId}`);

    let newPosition = positionOfImg;

  
    //TODO ващемта дело в том что сналало запускаеться функция чек и так и остаеться 
    useEffect(() => {
        apartment ? timer(apartment) : cheackTimer(apartment);
    }, [apartment]);

    //TODO надо учистить cheackTimer
    function cheackTimer(apartment: any) {
        let timerIdCheck = setTimeout(function run() {
            console.log('cheackTimer');
            console.log(`timerIdCheck = ${timerIdCheck}`);
            setRecursiveCheackTimerId(timerIdCheck);

            if (apartment) {                
                clearTimeout(timerIdCheck) 
                clearTimeout(recursiveCheackTimerId)
                timer(apartment);
            } else {
                timerIdCheck = setTimeout(cheackTimer, 5000);
            }
        }, 5000);
    }

    function timer(apartment: any) {
        let timerId = setTimeout(function run() {
            // if (recursiveTimeoutIsWorking === true) {
                console.log(newPosition);
                setRecursiveTimeoutId(timerId);


                ++newPosition;
                if (apartment.whatsOutside.length - 1 < newPosition) {
                    console.log('if');
                    newPosition = 0;
                    setPositionOfImg(() => 0);
                } else {
                    console.log('else');
                    setPositionOfImg(() => newPosition);
                }
                timerId = setTimeout(run, 5000);
            // }else{
            //     console.log(`timerId = ${timerId}`)
            // }
            //TODO а воттут надо стоп напсать
        }, 5000);
    }


    const changePositionImgOnClick = () => {
        const stopRecursiveTimeout = () => {
            setRecursiveTimeoutIsWorking(false);
            console.log(`recursiveTimeoutId = ${recursiveTimeoutId}`);
            console.log(recursiveCheackTimerId);

            clearTimeout(recursiveCheackTimerId + 4);
            clearTimeout(recursiveTimeoutId! + 2);
        };
        stopRecursiveTimeout();
    };

    // TODO где-то тут функция вызиваеться триллион раз
    // фуннкция вызиваеться сначало 1 раз потом 2 , 4 , 8 и тд
    function intChangePositionOfImg() {
        console.log(apartment);
        if (apartment != undefined) {
            let int = setTimeout(function tick() {
                function run(apartment: any) {
                    console.log('stil work');

                    ++newPosition;
                    console.log(positionOfImg);

                    setIntervalId(int);

                    if (apartment) {
                        console.log(apartment.whatsOutside.length);

                        console.log(newPosition);

                        if (apartment.whatsOutside.length - 1 < newPosition) {
                            console.log('if');
                            setPositionOfImg(() => 0);
                        } else {
                            console.log(apartment.whatsOutside.length);
                            console.log('else');

                            setPositionOfImg(() => newPosition);
                        }
                    } else {
                        console.log(apartment);
                    }
                }
                run(apartment);

                int = setTimeout(tick, 2000);
            }, 2000);
        }

        //return () => clearInterval(int)
    }











    
    const renderImg = () => {
        const imgs = apartment.whatsOutside;
        console.log(positionOfImg);
        let picUrl = imgs[positionOfImg].url;

        return (
            <>
                <img className="whatsOutside-image" src={picUrl} />
            </>
        );
    };

    return (
        <>
            <Row>
                <Col lg={{ offset: 1, span: 10 }}>
                    <div className="whatsOutside-body">
                        <div className="whatsOutside-showcase">
                            <div className="whatsOutside-swipeLeft" onClick={() => changePositionImgOnClick()}>
                                {positionOfImg}

                                {'<<<<<<<'}
                            </div>
                            {apartment ? renderImg() : <div>bad</div>}
                            <div className="whatsOutside-swipeRight" onClick={() => setPositionOfImg(positionOfImg + 1)}>
                                right{'>>>>>'}
                            </div>
                        </div>
                    </div>
                </Col>
            </Row>
        </>
    );
};

const mapStateToProps = (state: RootStateOrAny) => ({
    apartments: state.apartmentsArray.apartments,
    indexOfSelectedApartment: state.apartmentsArray.indexOfSelectedApartment,
});

export default connect(mapStateToProps, null)(WhatsOutside);
