import axios from 'axios';
import React, { Component, useState } from 'react';
import { Row, Col } from 'react-bootstrap';
import { connect, RootStateOrAny } from 'react-redux';
import { actions } from '../actions and const/actions';
import Showcase from './Showcase';
import img from '../picturesForApartment1/bathroom_1.jpg';
import { asyncActions } from '../actions and const/asyncActions';

function importAll(r: any) {
    return r.keys().map(r);
}

const pictures = importAll(require.context(`../picturesForApartments/picturesForApartment1`, false, /\.(png|jpe?g|svg)$/));

const f1 = (function () {
    let counter = 0;
    return function () {
        return ++counter;
    };
})();

f1();
f1();
f1();

class ApartmentShowcase extends React.Component<any, any> {
    componentDidMount() {
        axios
            .get('https://my-json-server.typicode.com/GreenScreenRabbit/constructor-comp-server/apartments')
            .then((response) => this.props.setApartmentsFromState(this.takeResponseAboutApartments(response.data, pictures)));
    }

    // TODO надо переписать для всех apartments
    takeResponseAboutApartments = (apartments: any, pictures: any) => {
        let idOfApartment: number = 0;

        let preparationApartmentToExport = (pictures: any) => {
            idOfApartment++;

            let filtredApartment = apartments.find((apartment: any) => apartment.id == idOfApartment);

            const picturesWithId: any[] = [];
            const namesRoomsArr: string[] = [];
            const rooms = filtredApartment.rooms;
            const picturesHouseItself = filtredApartment.picturesHouseItself;

            console.log(picturesHouseItself);

            console.log(rooms);

            //add all roomNames
            rooms.map((room: any) => {
                namesRoomsArr.push(room.name);
            });

            const pictureHouseItselfRegExp = new RegExp(`\d*\D*houseItself`, `gm`);
            const namesRoomsArrWithRegExp = namesRoomsArr.map((name: string) => new RegExp(`\d*\D*${name}`, `gm`));

            console.log(`pictureHouseItselfRegExp = ${pictureHouseItselfRegExp}`);

            //add images to id
            let id: number = 0;
            pictures.forEach((picture: any) => {
                console.log(picture);
                ++id;

                picture = picture.default;

                let pictureWithId = {
                    picture,
                    id,
                };
                picturesWithId.push(pictureWithId);
                console.log(picturesWithId);
            });

            console.log(rooms);

            const addImagesForApartments = (room: any, regExpArr: any): void => {
                // let regExpSelectedRoom: any = namesRoomsArrWithRegExp.find((regExp: any) => {
                //     if (!!room.name.match(regExp)) {
                //         console.log(regExp);

                //         return regExp;
                //     }
                // });

                console.log(regExpArr);

                    //TODO надо создать корированую функцию у которой все зависит от токо array это или нет

                    // const regExpSelectedRoom2: any = (regExpArr:any) => {
                    //     if(Array.isArray(regExpArr)){
                    //         console.log("arr");
                            
                    //     }else{
                    //         console.log("UNarrr");
                    //     }
                    // }

                    
                    regExpSelectedRoom2(regExpArr)


                    let regExpSelectedRoom: any = regExpArr.find((regExp: any) => {
                        if (!!room.name.match(regExp)) {
                            console.log(regExp);

                            return regExp;
                        }
                    });
               

                //add Images to ArrayImages
                picturesWithId.map((pictureWithId: any) => {
                    if (pictureWithId.picture.match(regExpSelectedRoom) != null) {
                        room.pictures.push(pictureWithId);
                    }
                });
            };

            //add images to apartment
            rooms.forEach((room: any) => {
                addImagesForApartments(room, namesRoomsArrWithRegExp);
                addImagesForApartments(room, pictureHouseItselfRegExp);
            });

            //add pictures to houseItself
        };

        preparationApartmentToExport(pictures);

        return apartments;
    };

    render() {
        const renderImages = () => {
            return (
                <div className="apartmentShowcase-overview" onMouseLeave={() => this.props.setIntervalForPicture(false)}>
                    {this.props.apartments.map((apartment: any, index: number) => {
                        if (index == this.props.indexOfSelectedApartment) {
                            if (this.props.indexOfSelectedRoom != null) {
                                return (
                                    <img
                                        src={apartment.rooms[this.props.indexOfSelectedRoom].pictures[this.props.positionOfPicture].picture}
                                        className="apartmentShowcase-overview-image"
                                    />
                                );
                            } else {
                                //TODO а вот тут мы по дефолту выводим дом
                                return <div className="apartmentShowcase-overview-image">Else</div>;
                            }
                        }
                    })}

                    <div>
                        <Showcase />
                    </div>
                </div>
            );
        };

        const chengeIndexOfSelectedApartment = (addOne: boolean) => {
            if (this.props.indexOfSelectedApartment == 0) {
                this.props.setIndexOfSelectedApartment(this.props.apartments.length - 1);
            } else if (this.props.indexOfSelectedApartment == this.props.apartments.length - 1) {
                this.props.setIndexOfSelectedApartment(0);
            } else if (addOne == true) {
                this.props.setIndexOfSelectedApartment(this.props.indexOfSelectedApartment + 1);
            } else if (addOne == false) {
                this.props.setIndexOfSelectedApartment(this.props.indexOfSelectedApartment - 1);
            }
        };

        const renderUpbar = () => {
            if (this.props.apartments[this.props.indexOfSelectedApartment] != undefined) {
                return (
                    <>
                        <div className="apartmentShowcase-frame" onMouseLeave={() => this.props.setIndexOfSelectedRoom(null)}>
                            <div className="apartmentShowcase-upBar">
                                <div className="apartmentShow ase-upBar-apartmentName">{this.props.apartments[this.props.indexOfSelectedApartment].name}</div>
                                <div className="apartmentShowcase-upBar-button-back" onClick={() => chengeIndexOfSelectedApartment(false)}>
                                    BACK
                                </div>
                                <div className="apartmentShowcase-upBar-button-next" onClick={() => chengeIndexOfSelectedApartment(true)}>
                                    NEXT
                                </div>
                            </div>

                            {renderImages()}
                        </div>
                    </>
                );
            }
        };

        return (
            <>
                <Row>
                    <Col lg={{ span: 10, offset: 1 }}>{renderUpbar()}</Col>
                </Row>
            </>
        );
    }
}

let mapStateToProps = (state: RootStateOrAny) => ({
    indexOfSelectedApartment: state.apartmentsArray.indexOfSelectedApartment,
    indexOfSelectedRoom: state.showcaseStylesAndIndex.indexOfSelectedRoom,
    showCaseApartment: state.apartmentShowCase.showCaseApartment,
    apartments: state.apartmentsArray.apartments,
    isIntervalForPictureWorking: state.showcaseStylesAndIndex.isIntervalForPictureWorking,
    positionOfPicture: state.showcaseStylesAndIndex.positionOfPicture,
});

export default connect(mapStateToProps, {
    setIndexOfSelectedApartment: actions.setIndexOfSelectedApartment,
    setApartmentsFromState: actions.setApartmentsFromState,
    setIndexOfSelectedRoom: actions.setIndexOfSelectedRoom,
    changePositionPictureOfRoom: asyncActions.changePositionPictureOfRoom,
    setIntervalForPicture: actions.setIntervalForPicture,
})(ApartmentShowcase);
